              <div className={styles.ntwrk}><div style={{color: 'white'}}>Current Network:</div>
                  {chainId === 1 ? <div>Ethereum</div> :
                      <>{chainId === 8 ? <div>Polygon</div> : <div>Not Connected to Polygon or Ethereum</div>}</>} {chainId}</div>
              <div className={styles.inner}>{children}</div>
